#!/usr/bin/env node

/**
 * @file
 * preview-barcodes.js
 *
 * Creates an HTML page to view barcodes generated by generate-barcodes.js.
 */

const fs = require("fs");
const path = require("path");

const barcodePath = path.join(__dirname, "../barcodes");
const ignoreFiles = [".DS_Store", "README.md"];
const previewFile = `${barcodePath}/index.html`;

// Remove any older preview files.
if (fs.existsSync(previewFile)) {
  fs.unlinkSync(previewFile);
}

fs.readdir(barcodePath, (err, files) => {
  if (err) {
    console.error("Unable to read directory:", err);
    return;
  }
  Object.values(files).forEach((file) => {
    if (!ignoreFiles.includes(file)) {
      const videoFile = file.replace(".svg", ".mp4");
      const img = `<p><img src="${file}" style="height: 50px;" /><br>${videoFile}</p>`;
      fs.appendFileSync(`${previewFile}`, img, (error) => {
        if (error) {
          console.error("Unable to write to file:", error);
        }
      });
    }
  });
});
